package com.example.tasbehimport android.content.Contextimport android.content.SharedPreferencesobject MySharedPrefereces {    private const val NAME = "catch_file_name"    private const val MODE = Context.MODE_PRIVATE    private lateinit var preferences: SharedPreferences    fun init(context: Context) {        preferences = context.getSharedPreferences(NAME, MODE)    }    private inline fun SharedPreferences.edit(operation: (SharedPreferences.Editor) -> Unit) {        val editor = edit()        operation(editor)        editor.apply()    }    var count: Int        get() = preferences.getInt("keyCount", 0)        set(value) = preferences.edit {            it.putInt("keyCount", value)        }}